mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name as category_name from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%', #{name}, '%')
and s.category_id = #{categoryId}
and s.status = #{status}
order by s.create_time

mapper.update:
update setmeal
update_time = #{updateTime}, update_user = #{updateUser}, category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image},
where id = #{id}
